console.warn(products);
// console.warn(ProductItem);


const HomeScreen = () => {
  return (
      <View  style={styles.page}>
         {/* <ProductItem item={products[4]} /> */}

         <FlatList 
         data={products}
         renderItem={({item}) => <ProductItem item={item} />}
