import React from 'react';
import { StyleSheet, Text, View, useColorScheme, SafeAreaView, StatusBar, Image } from 'react-native';
import {Colors} from 'react-native/Libraries/NewAppScreen';

const HomeScreen = () => {
    return (
        <View>
            {/* render product component */}
            <View style={styles.root}>
                <Image style={styles.image} source={{ uri: 'https://reactnative.dev/img/tiny_logo.png', }} />
                
                <View style={styles.rightContainer}>
                    <Text style={styles.title}>Lorem ipsum dolor, sit amet consectetur adipisicing elit. Quae, iste!</Text>
                    <Text style={styles.price}> from $25.22</Text>
                </View>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    root:{
       flexDirection: 'row',
       borderWidth: 1,
       borderColor: '#d1d1d1',
       borderRadius: 10,
       backgroundColor: '#fff',
       width: '100%'
    },
    image:{
        width: 150,
    height: 150,
    }, 
    rightContainer: {
     padding: 10,
     backgroundColor: 'red',
     width: '100%'
    },
    title: {
     width: '100%'
    },
