import React from 'react';
import { StyleSheet, Text, View, useColorScheme, SafeAreaView, StatusBar, Image } from 'react-native';
import {Colors} from 'react-native/Libraries/NewAppScreen';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
 
const HomeScreen = () => {
    return (
        <View style={styles.page}>
            {/* render product component */}
            <View style={styles.root}>
                <Image style={styles.image} source={{ uri: 'https://reactnative.dev/img/tiny_logo.png', }} />
                
                <View style={styles.rightContainer}>
                    <Text style={styles.title} numberOfLines={3}>Lorem ipsum dolor, sit amet consectetur adipisicing elit. Quae, iste!</Text>
                    {/* <View style={styles.ratingsContainer}>
                    <FontAwesomeIcon name="start" size={18} color={'#e47911'} />
                    </View> */}
                    <Text style={styles.price}> from $25.22</Text>
                </View>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    page:{
     padding: 10,
    },
    root:{
       flexDirection: 'row',
       borderWidth: 1,
       borderColor: '#d1d1d1',
       borderRadius: 10,
       backgroundColor: '#fff',
       width: '95 %'
    },
    image:{
        flex: 1,
        height: 150,
        resizeMode: 'contain', 
    }, 
    rightContainer: {
         padding: 10,
         flex: 3,
    },
    title: {
        fontSize: 18,
    },
    ratingsContainer: {

    },
    price: {
        fontSize: 18,
        fontWeight: 'bold',
    }
  });
export default HomeScreen;
